			stopwatch.Start();
			double delta = 1000 / 60D;
			double target = delta;
			while (!Netplay.disconnect)
			{
				{
					if (Main.oldStatusText != Main.statusText)
					{
						Main.oldStatusText = Main.statusText;
						Console.WriteLine(Main.statusText);
