					}
					else
					{
						Netplay.OnUpdate();
					}
					if (Main.OnTick != null)
					{
						Main.OnTick();
					}
					double now = stopwatch.ElapsedMilliseconds;
					double remaining = target - now;
					target += delta; //new target
					if (target < now) //can't catch up, reset target
					{
						target = now + delta;
					}
					Thread.Sleep(Math.Max((int)remaining, 0));
				}
			}
