						NetMessage.SendData(7, -1, -1, "", 0, 0f, 0f, 0f, 0, 0, 0);
					}
					else if (text == "noon")
					{
						Main.dayTime = true;
						Main.time = 27000.0;
						NetMessage.SendData(7, -1, -1, "", 0, 0f, 0f, 0f, 0, 0, 0);
					}
					else if (text == "midnight")
					{
						Main.dayTime = false;
						Main.time = 16200.0;
						NetMessage.SendData(7, -1, -1, "", 0, 0f, 0f, 0f, 0, 0, 0);
					}
					else if (text == "exit-nosave")
					{
						Netplay.disconnect = true;
					}
					else if (text == "exit")
					{
						WorldFile.saveWorld();
						Netplay.disconnect = true;
						SocialAPI.Shutdown();
					}
					else if (text == "fps")
					{
						if (!Main.dedServFPS)
						{
							Main.dedServFPS = true;
							Main.fpsTimer.Reset();
						}
						else
						{
							Main.dedServCount1 = 0;
							Main.dedServCount2 = 0;
							Main.dedServFPS = false;
						}
					}
					else if (text == "save")
					{
						WorldFile.saveWorld();
					}
					else if (text == "time")
					{
						string text3 = "AM";
						double num = Main.time;
						if (!Main.dayTime)
						{
							num += 54000.0;
						}
						num = num / 86400.0 * 24.0;
						double num2 = 7.5;
						num = num - num2 - 12.0;
						if (num < 0.0)
						{
							num += 24.0;
						}
						if (num >= 12.0)
						{
							text3 = "PM";
						}
						int num3 = (int)num;
						double num4 = num - (double)num3;
						num4 = (double)((int)(num4 * 60.0));
						string text4 = string.Concat(num4);
						if (num4 < 10.0)
						{
							text4 = "0" + text4;
						}
						if (num3 > 12)
						{
							num3 -= 12;
						}
						if (num3 == 0)
						{
							num3 = 12;
						}
						Console.WriteLine(string.Concat(new object[]
								{
									"Time: ",
									num3,
									":",
									text4,
									" ",
									text3
								}));
					}
					else if (text == "maxplayers")
					{
						Console.WriteLine("Player limit: " + Main.maxNetPlayers);
					}
					else if (text == "port")
					{
						Console.WriteLine("Port: " + Netplay.ListenPort);
					}
					else if (text == "version")
					{
						Console.WriteLine("Terraria Server " + Main.versionNumber + " - " + ModLoader.ModLoader.versionedName);
					}
					else
					{
						if (text == "clear")
						{
							try
							{
								Console.Clear();
								continue;
							}
							catch
							{
								continue;
							}
						}
						if (text == "playing")
						{
							int num5 = 0;
							for (int i = 0; i < 255; i++)
							{
								if (Main.player[i].active)
								{
									num5++;
									Console.WriteLine(string.Concat(new object[]
											{
												Main.player[i].name,
												" (",
												Netplay.Clients[i].Socket.GetRemoteAddress(),
												")"
											}));
								}
							}
							if (num5 == 0)
							{
								Console.WriteLine("No players connected.");
							}
							else if (num5 == 1)
							{
								Console.WriteLine("1 player connected.");
							}
							else
							{
								Console.WriteLine(num5 + " players connected.");
							}
						}
						else if (!(text == ""))
						{
							if (text == "motd")
							{
								if (Main.motd == "")
								{
									Console.WriteLine("Welcome to " + Main.worldName + "!");
								}
								else
								{
									Console.WriteLine("MOTD: " + Main.motd);
								}
							}
							else if (text.Length >= 5 && text.Substring(0, 5) == "motd ")
							{
								string text5 = text2.Substring(5);
								Main.motd = text5;
							}
							else if (text.Length == 8 && text.Substring(0, 8) == "password")
							{
								if (Netplay.ServerPassword == "")
								{
									Console.WriteLine("No password set.");
								}
								else
								{
									Console.WriteLine("Password: " + Netplay.ServerPassword);
								}
							}
							else if (text.Length >= 9 && text.Substring(0, 9) == "password ")
							{
								string text6 = text2.Substring(9);
								if (text6 == "")
								{
									Netplay.ServerPassword = "";
									Console.WriteLine("Password disabled.");
								}
								else
								{
									Netplay.ServerPassword = text6;
									Console.WriteLine("Password: " + Netplay.ServerPassword);
								}
							}
							else if (text == "say")
							{
								Console.WriteLine("Usage: say <words>");
							}
							else if (text.Length >= 4 && text.Substring(0, 4) == "say ")
							{
								string text7 = text2.Substring(4);
								if (text7 == "")
								{
									Console.WriteLine("Usage: say <words>");
								}
								else
								{
									Console.WriteLine("<Server> " + text7);
									NetMessage.SendData(25, -1, -1, "<Server> " + text7, 255, 255f, 240f, 20f, 0, 0, 0);
								}
							}
							else if (text.Length == 4 && text.Substring(0, 4) == "kick")
							{
								Console.WriteLine("Usage: kick <player>");
							}
							else if (text.Length >= 5 && text.Substring(0, 5) == "kick ")
							{
								string text8 = text.Substring(5);
								text8 = text8.ToLower();
								if (text8 == "")
								{
									Console.WriteLine("Usage: kick <player>");
								}
								else
								{
									for (int j = 0; j < 255; j++)
									{
										if (Main.player[j].active && Main.player[j].name.ToLower() == text8)
										{
											NetMessage.SendData(2, j, -1, "Kicked from server.", 0, 0f, 0f, 0f, 0, 0, 0);
										}
									}
								}
							}
							else if (text.Length == 3 && text.Substring(0, 3) == "ban")
							{
								Console.WriteLine("Usage: ban <player>");
							}
							else if (text.Length >= 4 && text.Substring(0, 4) == "ban ")
							{
								string text9 = text.Substring(4);
								text9 = text9.ToLower();
								if (text9 == "")
								{
									Console.WriteLine("Usage: ban <player>");
								}
								else
								{
									for (int k = 0; k < 255; k++)
									{
										if (Main.player[k].active && Main.player[k].name.ToLower() == text9)
										{
											Netplay.AddBan(k);
											NetMessage.SendData(2, k, -1, "Banned from server.", 0, 0f, 0f, 0f, 0, 0, 0);
										}
									}
								}
							}
							else
							{
								Console.WriteLine("Invalid command.");
							}
						}
					}
				}
				catch
				{
					Console.WriteLine("Invalid command.");
				}
			}
		}

		public static void Sundialing()
		{
			if (Main.sundialCooldown == 0)
			{
				if (Main.netMode == 1)
				{
					NetMessage.SendData(51, -1, -1, "", Main.myPlayer, 3f, 0f, 0f, 0, 0, 0);
					return;
				}
				Main.fastForwardTime = true;
				Main.sundialCooldown = 8;
				NetMessage.SendData(7, -1, -1, "", 0, 0f, 0f, 0f, 0, 0, 0);
			}
		}

		public static void UpdateSundial()
		{
			if (Main.fastForwardTime)
			{
				Main.dayRate = 60;
				return;
			}
			Main.dayRate = 1;
		}

