			for (int i = 0; i < drawCacheProjsBehindProjectiles.Count; i++)
			{
				int num = drawCacheProjsBehindProjectiles[i];
				if (Main.projectile[num].type == 628)
				{
					drawCacheProjsBehindProjectiles.Remove(num);
